function PodcastList({ podcasts }) {
  return (
    <div>
      {podcasts.map(podcast => (
        <Link href={`/podcast?id=${podcast.id}`}>
          <a href="podcast">
            <h3>{podcast.title}</h3>
            <div className="meta">
              {Math.ceil(podcast.duration / 60)} minutes
            </div>
          </a>
        </Link>
      ))}
    </div>
  );
}

export default PodcastList;
